.carousel {
  display: flex;
  justify-content: space-between;
}

.carousel__slide {
  position: relative;
  width: 100%;
  height: auto;
}

.carousel__button {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  padding: 1rem;
  background-color: #fff;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  transition: opacity 0.3s ease-in-out;
}

.carousel__button::before {
  content: "";
  position: absolute;
  top: -1rem;
  left: -1rem;
  width: 2rem;
  height: 2rem;
  border-radius: 50%;
  background-color: #fff;
}

.carousel__button:focus {
  outline: none;
}

.carousel__button--previous {
  left: 1rem;
}

.carousel__button--next {
  right: 1rem;
}

.carousel__slide img {
  width: 100%;
  height: auto;
  display: block;
}
